scripts.script:
def tarFileName = '${project.build.directory}/docker/${docker-image.namePrefix}/${docker-image.name}/tmp/docker-build.tar'
def tarFile = new File(tarFileName.replace(':', '/'))
assert tarFile.exists()
def sizeInM = (int) (tarFile.length() / (1024 * 1024))
def configuredExpectedSize = '${docker-contextTarFile.expectedSizeInMat10pct}'
int expectedSize = configuredExpectedSize ? configuredExpectedSize as int : 1
def pct = ((int) (1000 * sizeInM / expectedSize)) / 10
if ((pct >= 90 && pct <= 110) || (sizeInM + expectedSize) < 2) { log.info("Context tar file ok (" + pct + "% from configured expectation)")
}
else {
    throw new RuntimeException("Docker tar file outside expectations: " + sizeInM + "M/MiB (" + pct + "%): " + tarFile.absolutePath)
}
