import groovy.json.JsonSlurper

json = new JsonSlurper().parseText(messageExchange.response.responseContent)
assert !(json.isEmpty())

def documentUrl = context.testCase.testSuite.getPropertyValue("documentUrl")

if (documentUrl != "null"){
	def index = documentUrl.indexOf("{")
	def documentUrlShort = documentUrl.substring(0,index)

	def passed = false

	for(refText in json.refText){
		def referenzUrl = refText.referenzURL
		if (referenzUrl != null && referenzUrl.contains(documentUrlShort)){
			passed = true
		}
	}

	assert passed
}

