con:configuration.scriptText:
def response = context.expand('${GetFeatureInfo-html BP_Blan#Response}')

def matcher = (response =~ /href="(.*?)" class='externeReferenzProperty'/)
def url = matcher ? matcher[0][1] : null

def documentUrl = context.testCase.testSuite.getPropertyValue("documentUrl")

if (documentUrl != null){
	def index = documentUrl.indexOf("{")
	def documentUrlShort = documentUrl.substring(0,index)

	assert url.contains(documentUrlShort)
}

import groovy.xml.XmlSlurper

def xml = new XmlSlurper().parseText(messageExchange.response.responseContent).declareNamespace(xplan: 'http://www.deegree.org/xplanung/1/0',
wfs: 'http://www.opengis.net/wfs', gml: "http://www.opengis.net/gml/3.2")
assert !(xml.isEmpty())

def documentUrl = context.testCase.testSuite.getPropertyValue("documentUrl")

if (documentUrl != null){
	def index = documentUrl.indexOf("{")
	def documentUrlShort = documentUrl.substring(0,index)

	def externeRef = xml.'gml:featureMember'.'xplan:BP_Plan'.'xplan:externeReferenz'.text()

	assert externeRef.contains(documentUrlShort)
}

